import * as React from 'react';
import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';
import axios from 'axios';
import Container  from '@mui/material/Container';
import { styled, alpha } from '@mui/material/styles';
import InputBase from '@mui/material/InputBase';
import SearchIcon from '@mui/icons-material/Search';

export default function DisplayDrugs() {

   const [drugs, setDrugs] = React.useState([]);

   React.useEffect(() => {
     function allDrugs() {
        axios.get("http://localhost:8070/newdrugs/get").then((res) =>{
            console.log(res.data);
            setDrugs(res.data);
        }).catch((err) =>{
            alert(err.message);
        })
     } 
     allDrugs()
   },[])


  return (
    <div>
    <Container style={{marginLeft:'65px', marginRight:'25px', marginTop:'35px', maxWidth:'100%'}}>
            <Search>
            <SearchIconWrapper>
                <SearchIcon />
            </SearchIconWrapper>
            <StyledInputBase
                placeholder="Search Drug"
                inputProps={{ 'aria-label': 'search' }}
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                onKeyDown={(e) => {
                    if (e.key === 'Enter') {
                    handleSearch();
                    }
                }}
            />
            </Search>
        <IconButton aria-label="cart"  onClick={handleSearch} style={{marginLeft:10}}>
            <StyledBadge badgeContent={4} color="primary">
                <ShoppingCartIcon  fontSize='large'/>
            </StyledBadge>
        </IconButton>
        <Typography variant='h8' color='text.secondary' style={{marginLeft:'385px'}}>
                Your one stop shop for all your Medicinal needs..
        </Typography>
        <Link to="/onlinepharmacyP/displayDrugs">
            <Button variant="text">More</Button>
        </Link> 
</Container>

    <Container style={{marginTop :40, marginLeft:'48px'}}>
    <div style={{ display: 'flex', flexWrap: 'wrap', gap: '20px', justifyContent: 'center' }}>
      {drugs.map((drug, index) => (
            <Card key={index} sx={{ maxWidth: 345 }}>
                <CardMedia
                    sx={{ height: 140 }}
                    image="/static/images/cards/contemplative-reptile.jpg"
                    title="Drug Image"
                />
                <CardContent>
                    <Typography gutterBottom variant="h5" component="div">
                        {drug.name}
                    </Typography>
                    <Typography variant="body2" color="text.secondary">
                        {drug.description}
                    </Typography>
                    <div style={{marginTop : '10px'}}> 
                        <Typography variant="h8" color="text.primary" style={{fontWeight:'bold'}}>
                            Price: Rs.{drug.price}
                        </Typography>
                    </div>
                </CardContent>
                <CardActions>
                    <Button size="small"  variant='contained' style={{backgroundColor :'#002244'}} >Add To Cart</Button>
                </CardActions>
            </Card>
       ))}
     </div>
    </Container>

 </div>
  );
}
